import React from 'react';

// Try to import the moon icon, but provide fallback if it doesn't exist
let moonIcon;
try {
  moonIcon = require('../../assets/images/icons/moon.svg');
} catch (e) {
  // Fallback to an inline SVG if the image doesn't exist
  moonIcon = 'data:image/svg+xml;utf8,<svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M12.2256 21.8913C11.9016 21.9638 11.5689 22 11.2306 22C6.41249 22 2.5 18.0875 2.5 13.2694C2.5 8.45126 6.41249 4.53876 11.2306 4.53876C11.5689 4.53876 11.9016 4.575 12.2256 4.6475C9.97062 6.69 8.5 9.74876 8.5 13.2694C8.5 16.7913 9.97062 19.85 12.2256 21.8913ZM17.1694 2C20.0944 4.51876 22 8.38626 22 12.5C22 12.5862 22.0006 12.6731 22 12.7606C21.9875 12.7606 17.1694 12.7606 17.1694 12.7606C16.3462 12.7606 15.6788 12.0931 15.6788 11.27C15.6788 10.4469 16.3462 9.77938 17.1694 9.77938C17.1694 9.77938 22 9.77938 22 9.77938C21.4594 5.95126 19.28 2.90626 17.1694 2Z" fill="white"/></svg>';
}

const ThemeToggle = ({ isDarkMode, toggleTheme }) => {
  return (
    <div className="lightdark-switch">
      <span 
        className={`switch-btn ${isDarkMode ? 'light-btn' : 'dark-btn'}`}
        id="btnSwitch" 
        style={{ backgroundColor: 'rgb(0, 208, 148)' }}
        onClick={toggleTheme}
      >
        <img 
          src={moonIcon} 
          alt="light-dark-switchbtn" 
          className="swtich-icon"
        />
      </span>
    </div>
  );
};

export default ThemeToggle;
