import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Logo from './Logo';
import ThemeToggle from './ThemeToggle';
import styles from './Header.module.css';

const Header = () => {
  const [isSticky, setIsSticky] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 0);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const menuItems = [
    { id: 815, title: "Home", path: "/", isActive: true },
    { id: 816, title: "Statistics", path: "/statistics", isActive: false },
    { id: 1463, title: "Investments", path: "/investments", isActive: false },
    { id: 823, title: "Services", path: "/services", isActive: false },
    { id: 818, title: "About Us", path: "/about-us", isActive: false },
    { id: 819, title: "Contact Us", path: "/contact-us", isActive: false }
  ];

  return (
    <>
      <ThemeToggle />
      <header id="sticky-header" className={`header-section header-section--style3 ${isSticky ? 'header-fixed' : ''}`}>
        <div className="header-bottom">
          <div className="container">
            <div className="header-wrapper">
              <Logo />
              <div className={`menu-area menu--style2 ${isMobileMenuOpen ? 'active' : ''}`}>
                <ul id="menu-main-menu" className="menu menu">
                  {menuItems.map(item => (
                    <li 
                      key={item.id}
                      itemScope="itemscope"
                      itemType="https://www.schema.org/SiteNavigationElement"
                      id={`menu-item-${item.id}`}
                      className={`menu-item menu-item-type-post_type menu-item-object-page ${item.isActive ? 'current-menu-item page_item current_page_item active' : ''} menu-item-${item.id} nav-item`}
                    >
                      <Link 
                        title={item.title}
                        to={item.path}
                        className="nav-links"
                      >
                        {item.title}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
              <div className="header-action">
                <div className="menu-area">
                  <div className="header-btn">
                    <Link 
                      to="/Signup-Signin"
                      style={{
                        backgroundColor: '#00d094',
                        border: '1px solid #00d094'
                      }}
                      className="trk-btn trk-btn--border trk-btn--primary"
                    >
                      <span>Join Now</span>
                    </Link>
                  </div>
                  <div 
                    className={`header-bar d-lg-none header-bar--style2 ${isMobileMenuOpen ? 'active' : ''}`}
                    onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                  >
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
    </>
  );
};

export default Header;
