import React, { useEffect } from 'react';
import styles from './Header.module.css';

const ThemeToggle = () => {
  const [isDark, setIsDark] = React.useState(() => {
    return localStorage.getItem('bitrader-theme-mode') === 'dark';
  });

  useEffect(() => {
    const root = document.documentElement;
    root.setAttribute('data-bs-theme', isDark ? 'dark' : 'light');
    localStorage.setItem('bitrader-theme-mode', isDark ? 'dark' : 'light');
  }, [isDark]);

  const toggleTheme = () => {
    setIsDark(prev => !prev);
  };

  return (
    <div className="lightdark-switch">
      <span 
        className="switch-btn dark-btn"
        id="btnSwitch"
        onClick={toggleTheme}
        role="button"
        tabIndex={0}
        onKeyPress={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            toggleTheme();
          }
        }}
        style={{
          backgroundColor: isDark ? '#ffffff' : '#00D094',
          cursor: 'pointer',
          display: 'inline-flex',
          alignItems: 'center',
          justifyContent: 'center',
          width: '40px',
          height: '40px',
          borderRadius: '50%',
          border: 'none',
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
          transition: 'all 0.3s ease'
        }}
      >
        <img 
          src={isDark ? '/wp-content/themes/bitrader/assets/img/icons/sun.svg' : '/wp-content/themes/bitrader/assets/img/icons/moon.svg'}
          alt="light-dark-switchbtn"
          className="switch-icon"
          style={{
            width: '18px',
            height: '18px'
          }}
        />
      </span>
    </div>
  );
};

export default ThemeToggle;
