import React, { useEffect } from 'react';

// Import required CSS directly for the app
import '../App.css';

// This component ensures CSS is loaded in the right order
const CssLoader = () => {
  useEffect(() => {
    // Function to load CSS files from public directory
    const loadCssFile = (href, id) => {
      if (document.getElementById(id)) return; // Already loaded
      
      const link = document.createElement('link');
      link.rel = 'stylesheet';
      link.type = 'text/css';
      link.href = href;
      link.id = id;
      document.head.appendChild(link);
    };
    
    // Load theme CSS files
    loadCssFile('/css/bitrader-core.css', 'bitrader-core-css');
    loadCssFile('/css/bitrader-custom.css', 'bitrader-custom-css');
    loadCssFile('/css/aos.css', 'aos-css');
    
    // Initialize theme mode from localStorage if available
    const storedTheme = localStorage.getItem('theme-mode');
    if (storedTheme === 'dark') {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }

    // Add any additional initialization needed for other scripts
    // For example, initializing AOS (Animate On Scroll) if used
    if (typeof window.AOS !== 'undefined') {
      window.AOS.init();
    }
    
    // Clean up on unmount
    return () => {
      // Optional: Remove CSS when component unmounts
      // const core = document.getElementById('bitrader-core-css');
      // if (core) core.remove();
    };
  }, []);

  return null; // This component doesn't render anything
};

export default CssLoader;
