import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header/Header';
import CssLoader from './components/CssLoader';
import JsLoader from './components/JsLoader';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Error boundary component to catch errors in rendering
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error caught by boundary:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return <div className="error-page">Something went wrong. Please try again.</div>;
    }
    return this.props.children;
  }
}

function App() {
  return (
    <ErrorBoundary>
      <Router>
        <div className="App">
          <CssLoader />
          <JsLoader />
          <Header />
          <main className="main-content">
            <Routes>
              <Route path="/" element={
                <div className="container mt-5 pt-5 text-center">
                  <h1>BitTrader</h1>
                  <p>Your cryptocurrency trading platform</p>
                </div>
              } />
              {/* Add more routes as needed */}
            </Routes>
          </main>
        </div>
      </Router>
    </ErrorBoundary>
  );
}

export default App;
