import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';
import { menuItems } from '../constants/menuItems';

// Import CSS files
import '../assets/css/bootstrap.mind1c0.css';
import '../assets/css/bitrader-cored1c0.css';
import '../assets/css/bitrader-customd1c0.css';
import '../assets/css/bitrader-fontsd1c0.css';
import '../assets/css/aosd1c0.css';

// Import images
import LogoDark from '../assets/img/logo/logo-dark.png';
import LogoLight from '../assets/img/logo/logo-light.png';
import MoonIcon from '../assets/img/icons/moon.svg';
import SunIcon from '../assets/img/icons/sun.svg';

const Header = () => {
  const { isDarkMode, toggleTheme } = useTheme();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isSticky, setIsSticky] = useState(false);
  const headerRef = useRef(null);
  const location = useLocation();

  // Handle sticky header
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setIsSticky(true);
      } else {
        setIsSticky(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu on route change
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  // Handle scroll to top
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  // Handle mobile menu click outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (headerRef.current && !headerRef.current.contains(event.target)) {
        setIsMobileMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <>
      <header 
        ref={headerRef}
        id="sticky-header" 
        className={`header-section header-section--style3 ${isSticky ? 'sticky-menu' : ''}`}
      >
        <div className="header-bottom">
          <div className="container">
            <div className="header-wrapper">
              <div className="logo">
                <Link to="/" className="secondary-logo">
                  <img 
                    src={isDarkMode ? LogoLight : LogoDark}
                    style={{ maxHeight: '30px' }} 
                    alt="Bitrader Logo" 
                  />
                </Link>
              </div>

              <div className={`menu-area menu--style2 ${isMobileMenuOpen ? 'active' : ''}`}>
                <ul id="menu-main-menu" className="menu">
                  {menuItems.map((item) => (
                    <li 
                      key={item.id}
                      className={`menu-item menu-item-type-post_type menu-item-object-page ${
                        location.pathname === item.path ? 'current-menu-item page_item active' : ''
                      }`}
                    >
                      <Link to={item.path} className="nav-links">{item.title}</Link>
                    </li>
                  ))}
                </ul>
              </div>

              <div className="header-action">
                <div className="menu-area">
                  <div className="header-btn">
                    <Link 
                      to="/signup" 
                      style={{backgroundColor: '#00d094', border: '1px solid #00d094'}} 
                      className="trk-btn trk-btn--border trk-btn--primary"
                    >
                      <span>Join Now</span>
                    </Link>
                  </div>
                  <div 
                    className="header-bar d-lg-none header-bar--style2"
                    onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                  >
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Theme Toggle Button */}
      <div className="lightdark-switch">
        <span 
          className={`switch-btn ${isDarkMode ? 'dark-btn' : 'light-btn'}`}
          id="btnSwitch" 
          onClick={toggleTheme}
          style={{ backgroundColor: 'rgb(0, 208, 148)' }}
        >
          <img 
            src={isDarkMode ? SunIcon : MoonIcon}
            alt="theme-toggle" 
            className="switch-icon"
          />
        </span>
      </div>

      {/* Scroll to Top Button */}
      {isSticky && (
        <button 
          onClick={scrollToTop}
          className="scrollToTop scrollToTop--style2 scroll__top scroll-to-target scrollToTop--home2"
          aria-label="Scroll to top"
        >
          <i className="fas fa-angle-up"></i>
        </button>
      )}

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-menu-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}
    </>
  );
};

export default Header;
