import React, { useEffect } from 'react';

// Import theme-mode.js functionality - try/catch to handle if file doesn't exist
try {
  require('../assets/js/theme-mode');
} catch (e) {
  console.warn('theme-mode.js not found');
}

// This component handles loading and initializing WordPress JavaScript files
const JsLoader = () => {
  useEffect(() => {
    // Create script elements for any additional JS files
    const loadExternalScript = (src, id) => {
      if (document.getElementById(id)) return; // Already loaded
      
      const script = document.createElement('script');
      script.src = src;
      script.id = id;
      script.async = true;
      document.body.appendChild(script);
    };

    // Try to load additional scripts if they exist
    // Check if jquery exists in assets
    try {
      const jqueryPath = require('../assets/js/jquery.min.js');
      loadExternalScript(jqueryPath, 'jquery');
    } catch (e) {
      console.warn('jQuery not found in assets');
    }
    
    // Bootstrap JS if needed
    try {
      const bootstrapPath = require('../assets/js/bootstrap.bundle.min.js');
      loadExternalScript(bootstrapPath, 'bootstrap');
    } catch (e) {
      console.warn('Bootstrap JS not found in assets');
    }

    // Clean up on unmount
    return () => {
      // Remove any scripts that should be cleaned up
    };
  }, []);

  return null; // This component doesn't render anything
};

export default JsLoader;
