import React from 'react';
import { useTheme } from '../../context/ThemeContext';
import { ReactComponent as MoonIcon } from '../../assets/img/icons/moon.svg';
import { ReactComponent as SunIcon } from '../../assets/img/icons/sun.svg';
import styles from './Header.module.css';

const ThemeToggle = () => {
  const { theme, toggleTheme } = useTheme();
  const isDark = theme === 'dark';

  return (
    <div className={styles.lightdarkSwitch}>
      <span 
        className={styles.switchBtn}
        id="btnSwitch"
        onClick={toggleTheme}
        style={{
          backgroundColor: isDark ? '#ffffff' : '#00d094'
        }}
        role="button"
        tabIndex={0}
        onKeyDown={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            toggleTheme();
          }
        }}
        aria-label={`Switch to ${isDark ? 'light' : 'dark'} mode`}
      >
        {isDark ? <SunIcon className={styles.switchIcon} /> : <MoonIcon className={styles.switchIcon} />}
      </span>
    </div>
  );
};

export default ThemeToggle;
